def calc_fitness(pop, dicionario):

    from math import sin, sqrt

    for index, elem in enumerate(pop):

        # só atribui a fitness a cromossomos que ainda não possuem fitness
        # print(elem[0], elem[1])
        # x = to_decimal(elem[0])
        # y = to_decimal(elem[1])
        x = dicionario[elem[0]]
        y = dicionario[elem[1]]
        f6 = 0.5 - ((sin(sqrt(x**2 + y**2)))**2 - 0.5) / (1 + 0.001 * (x**2 + y**2))**2
        pop[index] = [f6, elem]

    return 0

